<?php/** 文件名:msg_manage.php* 功能:管理留言的功能* 更新日志:* 2012.11.12    黄  代码整理*/	$dep = checkDep();	require_once(ROOT_PATH."inc/table_config.php");	$sqlSelArt = "SELECT COUNT(*) AS `nums` FROM `".$messagetable."`";	$resSelArt = $db->query($sqlSelArt);	$objSelArt = $db->fetch_object($resSelArt);	$nums = $objSelArt->nums;	$max = 20;	@$_GET['page'] = ($_GET['page'] < 1)? (($_POST['page'] < 1)? 1: ($_POST['page'] > floor(($nums - 1) / $max) + 1? floor(($nums - 1) / $max) + 1: $_POST['page'])): ($_GET['page'] > floor(($nums - 1) / $max) + 1? floor(($nums - 1) / $max) + 1: $_GET['page']);	$sqlSelArt = "SELECT * FROM `".$messagetable."`         ORDER BY         `is_locked` DESC,         `father_id` ASC,         `msg_add_time` DESC,         `msg_clicked` DESC,         `mid` ASC,         `mid` LIMIT ".(($_GET['page'] - 1) * $max).", ".$max."";	$resSelArt = $db->query($sqlSelArt);?><link rel="stylesheet" type="text/css" href="css/main.css"><table width='95%' align="center" cellspacing="1" cellpadding="3" class="i_table">    <tr>        <td colspan="9" align="center" class="head" >            <b>::留言管理::</b>        </td>    </tr>    <tr align="center" class="head_2">        <td width="5%">ID</td>        <td >留言内容</td>        <td width="10%">添加者IP</td>        <td width="20%">时间</td>        <td width="5%">编辑</td>        <td width="5%">删除</td>        <td width="5%">审核操作</td>    </tr>    <?php        while($objSelArt=$db->fetch_object($resSelArt)){    ?>    <tr align="center" class="b">        <td><?=$objSelArt->mid;?></td>        <td>            <?php echo cut($objSelArt->msg_content,0,50);?>        </td>        <td><?=$objSelArt->msg_add_ip;?></td>        <td><?php echo $objSelArt->msg_add_time == 0? "由系统创建时添加": date("Y/m/d H:i:s", $objSelArt->msg_add_time);?></td>        <td><a href="admin.php?action=<?=$messagefolder?>/msg_add&do=<?=$objSelArt->mid;?>">编辑</a></td>        <td><a href="admin.php?action=<?=$messagefolder?>/msg_do&del=<?=$objSelArt->mid;?>">删除</a></td>        <td>            <?php                if($objSelArt->is_locked=="yes"){            ?>            <a href="admin.php?action=<?=$messagefolder?>/msg_pass&pass=<?=$objSelArt->mid;?>"><span style="color:#FF0000;">通过</span></a>            <?php                 }else{            ?>            <a href="admin.php?action=<?=$messagefolder?>/msg_pass&unpass=<?=$objSelArt->mid;?>">隐藏</a>            <?php                }            ?>        </td>    </tr>    <?php        }    ?>    <tr class="b">    <td colspan="9" align="center"><?=page( $_GET['page'] ,$nums , "admin.php?action=".$_GET['action'] ,$max);?></td>    </tr></table><?php    require_once(ROOT_PATH."foot.php");?>